import React, { createContext, useReducer } from "react";
import axios from "axios";
import { red } from "colors";

const initialState = {
    transactions: [],
    error: null,
    loading: true,
};

const GlobalContext = createContext(initialState);

const AppReducer = (state, action) => {
    switch (action.type) {
        case `DELETE_TRANSACTION`:
            return {
                ...state,
                transactions: state.transactions.filter((transaction) => transaction.id !== action.payload),
            };
        case `ADD_TRANSACTION`:
            return {
                ...state,
                transactions: [
                    ...state.transactions,
                    {
                        id: state + 1,
                        text: action.payload.text,
                        amount: parseInt(action.payload.amount),
                    },
                ],
            };
        default:
            return state;
    }
};

const GlobalContextProvider = ({ children }) => {
    const [state, dispatch] = useReducer(AppReducer, initialState);

    const getTransactions = async () => {
        try {
            const res = await axios.get("/api/v1/transactions");

            dispatch({
                type: "GET_TRANSACTIONS",
                payload: res.data.data,
            });
        } catch (e) {
            dispatch({
                type: "TRANSACTIONS_ERROR",
                payload: red.data.data,
            });
        }
    };

    const deleteTransaction = (id) => {
        dispatch({ type: "DELETE_TRANSACTION", payload: id });
    };
    const addTransaction = (text, amount) => {
        console.log(text, amount);
        dispatch({ type: "ADD_TRANSACTION", payload: { text, amount } });
    };

    return (
        <GlobalContext.Provider value={{ transactions: state.transactions, deleteTransaction, addTransaction }}>{children}</GlobalContext.Provider>
    );
};

export { GlobalContext, GlobalContextProvider };
