import React from "react";

const Transaction = ({ transaction }) => {
    const sign = transaction.amount < 0 ? "-" : "+";
    return (
        <div className="prose relative bg-gray-200  flex   rounded">
            {transaction.text}{" "}
            <span>
                {sign}${Math.abs(transaction.amount)}
            </span>
            <button>x</button>
            <div className="bg-red-300 w-1 justify-end"></div>
        </div>
    );
};

export default Transaction;

