import React, { useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalState";

const AddTransaction = () => {
    const { addTransaction } = useContext(GlobalContext);

    const [titleInput, setTitleInput] = useState();
    const [priceInput, setPriceInput] = useState();

    const handleSubmit = (e) => {
        e.preventDefault();
        addTransaction(titleInput, priceInput);
        setTitleInput("");
        setPriceInput("");
    };
    return (
        <div>
            <h3>Add New Transaction</h3>
            <form onSubmit={(e) => handleSubmit(e)}>
                <div>
                    <label htmlFor="Text" class="block text-sm font-medium leading-5 text-gray-700">
                        Text
                    </label>
                    <input
                        class="form-input block w-full sm:text-sm sm:leading-5"
                        value={titleInput}
                        onChange={(e) => {
                            setTitleInput(e.target.value);
                        }}
                        type="text"
                    />
                </div>
                <div>
                    <label for="email">Email</label>
                    <div class="mt-1 relative rounded-md shadow-sm">
                        <input id="email" class="form-input block w-full sm:text-sm sm:leading-5" placeholder="you@example.com" />
                    </div>
                </div>
                <div>
                    <label htmlFor="" class="block text-sm font-medium leading-5 text-gray-700">
                        Amount
                    </label>
                    <div class="mt-1 relative rounded-md shadow-sm">
                        <input
                            class="form-input block w-full sm:text-sm sm:leading-5"
                            value={priceInput}
                            onChange={(e) => {
                                setPriceInput(e.target.value);
                            }}
                            type="number"
                        />
                    </div>
                </div>
                <button>Add Transaction</button>
            </form>
        </div>
    );
};

export default AddTransaction;
