import React, { useState } from "react";

const Transaction = ({ transaction }) => {
    const [hovered, setHovered] = useState(false);
    const sign = transaction.amount < 0 ? "-" : "+";
    const deleteButton = hovered && (
        <button className=" ease-linearabsolute h-full w-5 bg-red-700 rounded text-white leading-none" style={{ left: `${hovered ? 0 : -20}` }}>
            x
        </button>
    );
    return (
        <div
            className="prose relative bg-gray-200 h-10  flex  justify-between items-center rounded mb-1"
            onMouseEnter={() => setHovered(true)}
            onMouseLeave={() => setHovered(false)}>
            <div className="px-3 w-full  flex justify-between">
                <span>{transaction.text}</span>
                <span>
                    {sign}${Math.abs(transaction.amount)}
                </span>
            </div>
            {deleteButton}
            <div className={`bg-${transaction.amount < 0 ? "red" : "green"}-300 w-2 rounded-r h-full justify-end`}></div>
        </div>
    );
};

export default Transaction;

