import React from "react";

const Transaction = ({ transaction }) => {
    const sign = transaction.amount < 0 ? "-" : "+";
    return (
        <div className="prose relative bg-gray-200 h-10  flex  justify-between items-center rounded">
            <div className="px-3 ">
                {" "}
                {transaction.text}{" "}
                <span>
                    {sign}${Math.abs(transaction.amount)}
                </span>
                <button>x</button>
            </div>
            <div className="bg-red-300 w-1 rounded-r h-full justify-end"></div>
        </div>
    );
};

export default Transaction;

