import React, { createContext, useReducer } from "react";

const initialState = {
    transactions: [
        { id: 1, text: "flower", amount: -20 },
        { id: 2, text: "Salary", amount: 210 },
        { id: 3, text: "food", amount: 20 },
        { id: 4, text: "chair", amount: -40 },
    ],
};

const GlobalContext = createContext(initialState);

const AppReducer = (state, action) => {
    switch (action.type) {
        case `DELETE_TRANSACTION`:
            return {
                ...state,
                transactions: state.transactions.filter((transaction) => transaction.id !== action.payload),
            };
        case `ADD_TRANSACTION`:
            return {
                ...state,
            };
        default:
            return state;
    }
};

const GlobalContextProvider = ({ children }) => {
    const [state, dispatch] = useReducer(AppReducer, initialState);

    const deleteTransaction = (id) => {
        dispatch({ type: "DELETE_TRANSACTION", payload: id });
    };
    const addTransaction = (id) => {
        dispatch({ type: "add_TRANSACTION", payload: id });
    };

    return <GlobalContext.Provider value={{ transactions: state.transactions, deleteTransaction }}>{children}</GlobalContext.Provider>;
};

export { GlobalContext, GlobalContextProvider };
